function hslToRgb(e,d,c){var i,f,a;e=e%360;if(e<0){e+=360}e=~~e/360;if(d<0){d=0}else{if(d>100){d=100}}d=~~d/100;if(c<0){c=0}else{if(c>100){c=100}}c=~~c/100;if(d===0){i=f=a=c}else{var j=c<0.5?c*(1+d):c+d-c*d;var k=2*c-j;i=hueToRgb(k,j,e+1/3);f=hueToRgb(k,j,e);a=hueToRgb(k,j,e-1/3)}return[~~(i*255),~~(f*255),~~(a*255)]}function hueToRgb(c,b,a){if(a<0){a+=1}if(a>1){a-=1}if(a<1/6){return c+(b-c)*6*a}if(a<1/2){return b}if(a<2/3){return c+(b-c)*(2/3-a)*6}return c}function shortenHsl(a,f,g){var e=hslToRgb(a,f,g);var b=e[0].toString(16);var d=e[1].toString(16);var c=e[2].toString(16);return"#"+((b.length==1?"0":"")+b)+((d.length==1?"0":"")+d)+((c.length==1?"0":"")+c)}module.exports=shortenHsl;